mapper.insert:
INSERT
  INTO TBL_ORDER A
(
     A.ORDER_CODE
   , A.ORDER_DATE
   , A.ORDER_TIME
   , A.TOTAL_ORDER_PRICE  
)
VALUES
(
     SEQ_ORDER_CODE.NEXTVAL
   , #{date}
   , #{time}
   , #{totalPrice}  
)


	SELECT
		   SEQ_ORDER_CODE.CURRVAL
	  FROM DUAL	   

INSERT
  INTO TBL_ORDER_MENU A
(
  A.ORDER_CODE
, A.MENU_CODE
, A.ORDER_AMOUNT  
)  
VALUES
(
  #{code}
, #{menuCode}
, #{amount}  
)
